(function($){"use strict";$(document).ready(function(){$(window).on('elementor/frontend/init',function(){elementorFrontend.hooks.addAction('frontend/element_ready/uppsae-pricing-table-flip.default',function($scope){var wrapSelect=$scope.find(".pricing-wrapper");bouncy_filter(wrapSelect)})});function bouncy_filter(wrapper){var container=wrapper.find('.cd-pricing-container');container.each(function(){var pricing_table=$(this);var filter_list_container=wrapper.find(".cd-pricing-switcher-container").children(".cd-pricing-switcher"),filter_radios=filter_list_container.find('input[type="radio"]'),pricing_table_wrapper=pricing_table.find(".cd-pricing-wrapper");var table_elements={};filter_radios.each(function(){var filter_type=$(this).val();table_elements[filter_type]=pricing_table_wrapper.find('li[data-type="'+filter_type+'"]')});filter_radios.on("change",function(event){event.preventDefault();var selected_filter=$(event.target).val();show_selected_items(table_elements[selected_filter]);if(!Modernizr.cssanimations){hide_not_selected_items(table_elements,selected_filter);pricing_table_wrapper.removeClass("is-switched")}else{pricing_table_wrapper.addClass("is-switched").eq(0).one("webkitAnimationEnd oanimationend msAnimationEnd animationend",function(){hide_not_selected_items(table_elements,selected_filter);pricing_table_wrapper.removeClass("is-switched");if(pricing_table.find(".cd-pricing-list").hasClass("cd-bounce-invert"))pricing_table_wrapper.toggleClass("reverse-animation")})}})})}
function show_selected_items(selected_elements){selected_elements.addClass("is-selected")}
function hide_not_selected_items(table_containers,filter){$.each(table_containers,function(key,value){if(key!=filter){$(this).removeClass("is-visible is-selected").addClass("is-hidden")}else{$(this).addClass("is-visible").removeClass("is-hidden is-selected")}})}})})(jQuery)